class TreeNode:
    # tree node of a multi-way tree
    def __init__(self, val):
        self.val = val
        # children
        self.next = {}
        # mark this node as the end of a path to search a word
        self.is_end = False


class StreamChecker:
    # search tree data structure
    def __init__(self, words):
        # root of the search tree
        self.root = TreeNode(None)
        # a list to store unfinished paths
        self.q = []
        for word in words:
            # add each word into the search tree
            head = self.root
            for char in word:
                if char not in head.next:
                    head.next[char] = TreeNode(char)
                head = head.next[char]
            head.is_end = True

    def query(self, letter: str) -> bool:
        # add a new path
        self.q.append(self.root)
        next_q = []
        for each in self.q:
            if letter in each.next:
                next_q.append(each.next[letter])
        self.q = next_q
        return any([each.is_end for each in self.q])
