class Solution {
    public boolean isRobotBounded(String instructions) {
        char[] ins = instructions.toCharArray();

        int[] start = new int[]{0,0};

        Map<Character, int[]> map = new HashMap<>();
        map.put('N',new int[]{0,1});
        map.put('S',new int[]{0,-1});
        map.put('E',new int[]{1,0});
        map.put('W',new int[]{-1,0});
        char facing = 'N';
        int i =0;

        while(i < ins.length){
            if(ins[i] == 'G'){
                start[0] = start[0]+map.get(facing)[0];
                start[1] = start[1]+map.get(facing)[1];
            } else if(ins[i] == 'L'){
                if(facing == 'N') facing = 'W';
                else if(facing == 'W') facing = 'S';
                else if(facing == 'S') facing = 'E';
                else if(facing == 'E') facing = 'N';
            } else if(ins[i] == 'R'){
                if(facing == 'N') facing = 'E';
                else if(facing == 'E') facing = 'S';
                else if(facing == 'S') facing = 'W';
                else if(facing == 'W') facing = 'N';
            }
            i++;

        }
        if(start[0] == 0 && start[1] == 0) return true;
        if(facing == 'N') return false;// This means, robot will go out of circle
        return true;
    }
}
