const isRobotBounded = instructions => {
    let position = { x: 0, y: 0 };
    let orientation = 'north';

    for (const instruction of [...instructions]) {
        ({ position, orientation } = execute(instruction, position, orientation));
    }

    return orientation !== 'north' || (position.x === 0 && position.y === 0);
};

const execute = (instruction, position, orientation) => {
    switch (instruction) {
        case 'G':
            return goOneUnit(position, orientation);
        case 'L':
            return rotateLeft(position, orientation);
        case 'R':
            return rotateRight(position, orientation);
    }

    throw new Error(`Unsupported operation ${instruction}`);
};

const goOneUnit = (position, orientation) => {
    switch (orientation) {
        case 'north':
            return {
                position: {
                    x: position.x,
                    y: position.y + 1,
                },
                orientation,
            };
        case 'east':
            return {
                position: {
                    x: position.x + 1,
                    y: position.y,
                },
                orientation,
            };
        case 'south':
            return {
                position: {
                    x: position.x,
                    y: position.y -1,
                },
                orientation,
            };
        case 'west':
            return {
                position: {
                    x: position.x - 1,
                    y: position.y,
                },
                orientation,
            };
    }

    throw new Error(`Invalid orientation ${orientation}`);
};

const rotateLeft = (position, orientation) => {
    switch (orientation) {
        case 'north':
            return {
                position,
                orientation: 'west',
            };
        case 'west':
            return {
                position,
                orientation: 'south',
            };
        case 'south':
            return {
                position,
                orientation: 'east',
