        if (c == 'L') {
            if (dir == DIRECTION.NORTH) return DIRECTION.WEST;
            else if (dir == DIRECTION.WEST) return DIRECTION.SOUTH;
            else if (dir == DIRECTION.SOUTH) return DIRECTION.EAST;
            else if (dir == DIRECTION.EAST) return DIRECTION.NORTH;
