            if l == 'L':
                count = (count + 1) % 4
                d     = (d + 1) % 4

            if l == 'R':
                count = (count - 1) % 4
                d     = (d - 1) % 4

            if l == 'G':
                loc = (loc[0] + dirs[d][0], loc[1] + dirs[d][1])

        return count != 0 or loc == (0, 0)
