class Solution {
public:
    bool isRobotBounded(string instructions) {
        char dir='N';
        int x=0;int y=0;
        int n=instructions.size();
        for(int i=0;i<n;i++){

            //move straight
            if(instructions[i] == 'G'){
                if(dir == 'N') y+=1;
                else if(dir == 'S') y-=1;
                else if(dir == 'E') x+=1;
                else x-=1;
            }

            //turning to left direction
            else if(instructions[i] == 'L'){
                if(dir == 'N') dir='W';
                else if(dir == 'S') dir='E';
                else if(dir == 'E') dir='N';
                else dir='S';
            }

            //turning to right direction
            else{
                if(dir == 'N') dir='E';
                else if(dir == 'S') dir='W';
                else if(dir == 'E') dir='S';
                else dir='N';
            }            
        }
        if(x==0 && y==0) return true;       //means it returns to original points.
        if(dir == 'N') return false;               //since we are moving away from origin.
        return true;
    }
};
