                mx += directions[dir][0];
                my += directions[dir][1];
            }else if(ch == 'L'){
                if(dir == 0) dir = 1;
                else if(dir == 1) dir = 2;
                else if(dir == 2) dir = 3;
                else dir = 0;
            }else if(ch == 'R'){
                if(dir == 0) dir = 3;
                else if(dir == 3) dir = 2;
                else if(dir == 2) dir = 1;
