// In case of Left and right just change face.
// In case of 'G' just move towards face. 
class Solution {
    public boolean isRobotBounded(String instructions) {
        int x = 0;
        int y = 0;
        char[] fx = instructions.toCharArray();

        char face = 'N';
        int counter = 4;
        while(counter-- > 0){
            for (char c: fx) {
                if (c == 'L') {
                    if (face == 'N') face = 'W';
                    else if (face == 'W') face = 'S';
                    else if (face == 'S') face = 'E';
                    else face = 'N';
                }

                if (c == 'R') {
                    if (face == 'N') face = 'E';
                    else if (face == 'E') face = 'S';
                    else if (face == 'S') face = 'W';
                    else face = 'N';
                }

                if(c == 'G'){
                    if (face == 'N') y += 1;
                    if (face == 'S') y -= 1;
                    if (face == 'W') x -= 1;
                    if (face == 'E') x += 1;
                }
            }
            if(x == 0 && y == 0)break;
        }
