class Solution {
    public boolean isRobotBounded(String instructions) {
        int x = 0;
        int y = 0;
        int direction = 90; // North
        for (char inst : instructions.toCharArray()) {
            switch (inst) {
                case 'G':
                    if (direction == 90) y = y + 1; // North
                    if (direction == 270) y = y -1; // South
                    if (direction == 180) x = x - 1; // West
                    if (direction == 0) x = x + 1; // East
                    break;
                case 'L':
                    direction = Math.floorMod(direction - 90, 360);
                    break;
                case 'R':
                    direction = Math.floorMod(direction + 90, 360);
                    break;
            }
        }
        return (x == 0 && y == 0) || direction != 90;
    }
}

