```c++
class Solution {
public:
    bool isRobotBounded(string str) {
        int x=0;
        int y=0;
        int d=0;
        for(int i=0;i<str.size();i++){
            if(str[i] == 'L'){
                d = d+1 > 3 ? 0 : d+1;
            }else if(str[i] == 'R'){
                d = d-1 < 0 ? 3 : d-1;
            }else if(str[i] == 'G'){
                switch(d){
                    case 0 : y+=1;
                        break;
                    case 1 : x+=1;
                        break;
                    case 2 : y-=1;
                        break;
                    case 3 : x-=1;
                        break;
                    default: break;
                }
            }
        }

        return (x==0 && y==0) ? true : d!=0 ? true : false;
    }
};
```

