                dx = dy;
                dy = -temp;
                break;
            case 'L':
                temp = dx;
                dx = -dy;
                dy = temp;
                break;
            case 'G':
                x += dx;
                y += dy;
                break;
        } // end switch
    } // end for

    return (((x == 0) && (y == 0)) || ((dx != 0) || (dy != 1)));
} // end isRobotBounded
```

