func isRobotBounded(instructions string) bool {
	for i := 0; i < 2; i++ {
		instructions = instructions + instructions
	}
	directions := []byte{'N', 'W', 'S', 'E'}
	point_to_dir := 0
	coords := [2]int{0, 0}
	for _, instruction := range instructions {
		switch instruction {
		case 'G':
			direction := directions[point_to_dir]
			switch direction {
			case 'N':
				coords[1] += 1
			case 'S':
				coords[1] -= 1
			case 'W':
				coords[0] -= 1
			case 'E':
				coords[0] += 1
			}

		case 'L':
			if point_to_dir != len(directions)-1 {
				point_to_dir += 1
			} else {
				point_to_dir = 0
			}
		case 'R':
			if point_to_dir != 0 {
				point_to_dir -= 1
			} else {
				point_to_dir = len(directions) - 1
			}
		}
	}
	return coords[0] == 0 && coords[1] == 0
}
