class Solution {
    fun isRobotBounded(s: String): Boolean {
        var r = 0
        var c = 0
        var d = 0
        for (i in 0 until 4) {
            for (ch in s) {
                when (ch) {
                    'G' -> {
                        when (d) {
                            0 -> r++
                            1 -> c++
                            2 -> r--
                            else -> c--
                        }
                    }
                    'L' -> {
                        d--
                        if (d == -1) d = 3
                    }
                    else -> {
                        d++
                        if (d == 4) d = 0
                    }
                }
            }
        }

        return r == 0 && c == 0
    }
}
