impl Solution {
    pub fn last_stone_weight(stones: Vec<i32>) -> i32 {
        let mut heap = BinaryHeap::from(stones);
        while let Some(stone) = heap.pop() {
            match heap.pop() {
                Some(val)   => heap.push(stone - val),
                None        => return stone,
            }       
        }
        return 0
    }
}
