class Solution {
public:
    int lastStoneWeight(vector<int>& stones) {
        int n=stones.size();
        priority_queue<int> pq;
        for(int i=0;i<n;i++)
        {
            pq.push(stones[i]);
        }    
        while(pq.size()>1)
        {
            int a=pq.top();
            pq.pop();
            int b=pq.top();
            pq.pop();
            if(a!=b)
            {
                int c=a-b;
                pq.push(c);
            }
        }
        if(pq.size()==0)
        {
            return 0;
        }
        else
        {
            int l=pq.top();
            return l;
        }
    }
};
