class Solution {
    public int lastStoneWeight(int[] stones) {
        if(stones == null || stones.length < 1) {
            return 0;
        }
        if(stones.length == 1) {
            return stones[0];
        }
        Arrays.sort(stones);
        int i = stones.length - 2;
        int j = stones.length - 1;
        while(i >= 0 && j >= 0 && stones[i] != 0) {
            stones[i] = stones[j] - stones[i];
            stones[j] = 0;
            Arrays.sort(stones);
        }
        return stones[j];
    }
}
