        PriorityQueue<Integer> q = new PriorityQueue<>(Collections.reverseOrder());
        for(int i = 0; i < stones.length; i++) q.offer(stones[i]);
        while(q.size() > 1) {
            int a = q.poll();
            int b = q.poll();
            q.offer(a-b);
        }
        if(q.isEmpty()) return 0;
        else return q.poll();
    }
}
