func removeDuplicates(_ S: String) -> String {
        var result: String = ""

        for char in S {
            if result.isEmpty {
                result += String(char)
            }
            else if let peek = result.last {
                if char == peek {
                    result.removeLast()
                }
                else {
                    result += String(char)
                }
            }
        }

        return result
    }
