class Solution:
    def longestPalindrome(self, s):
        n, ans = len(s), ""
        def helper(i, j):
            while i >= 0 and j < n and s[i] == s[j]:
                i, j = i - 1, j + 1
            return s[i + 1:j]

        for k in range(n):
            ans = max(helper(k, k), helper(k, k + 1), ans, key=len)
        return ans
