// M-1 DP
// TC = SC = O(n^2)
class Solution {
public:
    string longestPalindrome(string s) {
        int n = s.length();
        if(n==1) return s;
        vector<vector<bool>> dp(n, vector<bool>(n, false));
        // max length of the pallindromic substring
        int max_length = 0;
        // starting index of the max length pallindromic substring
        int start_index = 0;
        for(int i = 0; i < n; i++)
        {
            dp[i][i] = true;
        }
        // check sub-strings of size 2
        for(int i = 0; i < n - 1; i++)
        {
            if(s[i] == s[i+1])
            {
                dp[i][i+1] = true;
                max_length = 2;
                start_index = i;
            }
        }
        // check sub-strings of size > 2
        // here k denotes the size of the sub-strings i.e. from 3 to n
        for(int k = 3; k <= n; k++)
        {
            for(int i = 0; i <= n - k; i++)
            {
                // i and j points to the starting & ending indices of the substring of size k
                int j = i + k - 1;
                if(s[i] == s[j] && dp[i+1][j-1] == true)
                {
                    dp[i][j] = true;
                    max_length = max(k, max_length);
                    start_index = i;
                }
            }
        }
        string ans = "";
        // if there is no pallindromic substring present of size >= 2 
        if(max_length == 0)
        {
            ans += s[0];
            return ans;
        }
        for(int i = start_index; i < start_index + max_length; i++)
        {
            ans += s[i];
        }
        return ans;
    }
};

// M-2 
// TC = O(n^2) SC = O(1)
class Solution {
public:
    string longestPalindrome(string s) {
        int n = s.length();
        if(n==1) return s;
        string ans = "";
        // low and high points to the starting & ending indices of the pallindromic substrings 
        int low, high;
        // starting index of the max length pallindromic substring
        int start_index;
        // max length of the pallindromic substring
        int max_length = 0;
        for(int i = 1; i < n; i++)
        {
            // for odd length substrings
            // choose i as epicenter and move left and right checking the string is pallindrome or not
            low = i - 1;
            high = i + 1;
            while(low >= 0 && high < n && s[low] == s[high])
            {
                if(max_length < high - low + 1)
                {
                    max_length = high - low + 1;
                    start_index = low;
                }
                --low;
                ++high;
            }
            // for even length substrings
            // choose i-1 and i as epicenters and move left and right checking the string is pallindrome or not
            low = i - 1;
            high = i;
            while(low >= 0 && high < n && s[low] == s[high])
            {
                if(max_length < high - low + 1)
                {
                    max_length = high - low + 1;
                    start_index = low;
                }
                --low;
                ++high;
            }
        }
        // if there is no pallindromic substring present of size >= 2 
        if(max_length == 0)
        {
            ans += s[0];
            return ans;
        }
        for(int i = start_index; i < start_index + max_length; i++)
        {
            ans += s[i];
        }
        return ans;
    }
};
