 class Solution {
public:
    vector<vector<int>> dp;
    int ans=1;
    int start=0;
    string str;
    bool lps(int i,int j){
        if(i>j) return false;
        if(i==j){
            return dp[i][j]=1;
        }

        if(dp[i][j]!=-1) return dp[i][j];

        if(str[i]==str[j] and (i==j-1 or lps(i+1,j-1))){
            dp[i][j]=1;
            int len=j-i+1;
            if(len>ans){
                ans=len;
                start=i;
            }
        }

        lps(i,j-1);
        lps(i+1,j);
        if(dp[i][j]==-1) dp[i][j]=0;
        return dp[i][j];
    }
    string longestPalindrome(string s) {
        int n=s.length();
        str=s;
        dp.resize(n+1,vector<int>(n+1,-1));
        lps(0,s.length()-1);
        return s.substr(start,ans);
    }
};
