var longestPalindrome = function(s) {
    let res = "";
    let cur = "";
    for(let i = 0; i < s.length; i ++){
        for(let j = i; j < i + 2; j ++){
            let left = i;
            let right = j;
            while(s[left] && s[left] === s[right]){
                cur = s.substring(left, right + 1);
                if(cur.length > res.length) res = cur;
                left --;
                right ++;
            }
        }
    }
    return res;
};
