    private int getDistance(char[] arr,int index1,int index2){
		int i1 = index1-1;
		int i2 = index2+1;
		int dist = 0;
		while(i1>=0&&i2<arr.length){
			if(arr[i1]==arr[i2]){
				dist++;
			}else{
				break;
			}
			i1--;i2++;
