class Solution {
public:
    string longestPalindrome(string s) {
        int n = s.size();
        int dp[n][n];
        memset(dp, 0, sizeof dp);

        int len = 0, start = 0;
        for (int g = 0; g < n; ++g) {
            for (int i = 0, j = g; j < n; ++i, ++j) {
                if (g == 0) dp[i][j] = true;
                else if (g == 1) dp[i][j] = s[i] == s[j];
                else dp[i][j] = s[i] == s[j] && dp[i + 1][j - 1];

                if (dp[i][j]) {
                    len = g + 1;
                    start = i;
                }
            }
        }

        return s.substr(start, len);
    }
};
