        if (pre_size == 0)
        {
            return NULL;
        }

        stack<TreeNode*> s;
        TreeNode* root = new TreeNode(preorder[0]);
        s.push(root);

        while (i < pre_size - 1)
        {
            TreeNode* node = s.top();
            if (node->val != inorder[j])
            {
                ++i;
                node->left = new TreeNode(preorder[i]);
                node = node->left;
                s.push(node);
            } 
            else
            {
                s.pop();
                ++j;
                if (s.empty() || s.top()->val != inorder[j])
                {
                    ++i;
                    node->right = new TreeNode(preorder[i]);
                    node = node->right;
                    s.push(node);
                }
            }
        }
        return root;
    }
