        guard let rootVal = preorder.first else { return nil }
        let root = TreeNode(rootVal)

        let i = inorder.index(of: rootVal)!
        let inorderL = Array(inorder[0..<i])
        let inorderR = Array(inorder[i+1..<inorder.count])
        let preorderL = Array(preorder[1..<i+1])
        let preorderR = Array(preorder[i+1..<preorder.count])

        root.left = buildTree(preorderL, inorderL)
        root.right = buildTree(preorderR, inorderR)
        return root
    }
}
