class Solution {
    func buildTree(_ preorder: [Int], _ inorder: [Int]) -> TreeNode? {
        return buildTree(preorder[0...], inorder[0...])
    }

    func buildTree(_ preorder: ArraySlice<Int>, _ inorder: ArraySlice<Int>) -> TreeNode? {
        guard let rootVal = preorder.first else { return nil }
        let root = TreeNode(rootVal)

        let i = inorder.index(of: rootVal)!
        let leftCount = i - inorder.startIndex
        let rightCount = inorder.endIndex - i - 1

        root.left = buildTree(
            preorder[preorder.startIndex+1..<preorder.startIndex+1+leftCount],
            inorder[inorder.startIndex..<inorder.startIndex+leftCount]
        )
        root.right = buildTree(
            preorder[preorder.endIndex-rightCount..<preorder.endIndex],
            inorder[inorder.endIndex-rightCount..<inorder.endIndex]
        )
        return root
    }
}
