// first index node of preorder will be root Node for building a Binary tree
// Now search for the first Index we got from preorder inside Inorder and the left of root will be the left part of the root from inorder
// and right to root will be the right elements from the inorder

class Solution {
public:

    unordered_map<int, int> mp;
    int preIndex = 0; // For iterating preorder array

    // is is the starting index of the inorder, ie is the ending index of the inorder
    TreeNode*buildFromPre(vector<int>& preorder, int is, int ie)
    {
        if(is > ie) // for particular recursive call there are no left or right subtrees
             return NULL;

        TreeNode* root = new TreeNode(preorder[preIndex++]); 

        if(is == ie)
            return root;

        int mid = mp[root->val]; // the mid will be the preorder first index 

        root->left =  buildFromPre(preorder, is, mid-1); // recursively call for starting index to mid-1, this will be the left subtree
        root->right = buildFromPre(preorder, mid+1, ie); // recursively call for mid+1 to ending index, this will be our right subtree

        return root;
    }

    TreeNode* buildTree(vector<int>& preorder, vector<int>& inorder) 
    {
        for(int i = 0; i < inorder.size(); i++)
            mp[inorder[i]] = i; // Map inorder elements as key to its indices as values

        return buildFromPre(preorder, 0, inorder.size()-1);
    }
};
