        TreeNode* root = new TreeNode(pre[preS]);
        int inMid = in_map[pre[preS]];
        preS++; // preoder traveral 
        root->left = helper(inStart, inMid -1,pre, in_map);
        root->right = helper(inMid +1, inEnd,pre, in_map);
        return root;
    }
};

