        imid = self.itable[self.preorder[pbegin]]
        lsize = imid - ibegin
        rsize = size - lsize - 1
        tree = TreeNode(self.preorder[pbegin])
        tree.left = self.Build(pbegin + 1, ibegin, lsize)
        tree.right = self.Build(pbegin + 1 + lsize, imid + 1, rsize)
