// TC - O(N^2) SC - O(1)
class Solution {
public:
    TreeNode* solve(vector<int>& preorder, vector<int>& inorder, int preStart, int inStart, int inEnd)
    {
        if(preStart >= preorder.size() || inStart > inEnd) return NULL;
        TreeNode* root = new TreeNode(preorder[preStart]);
        int inIndex = 0;
        // find the index of the root node in inorder array (we can use hashmap as well)
        for(int i = inStart; i <= inEnd; i++)
        {
            if(inorder[i] == root->val)
            {
                inIndex = i;
            }
        }
        // Now left part of inIndex will be left subtree of root and right part will be right subtree
        root->left = solve(preorder, inorder, preStart + 1, inStart, inIndex - 1);
        // skip left tree (inIndex - inStart) to calculate the index of the right root node
        root->right = solve(preorder, inorder, preStart + inIndex - inStart + 1, inIndex + 1, inEnd);
        return root;
    }
    TreeNode* buildTree(vector<int>& preorder, vector<int>& inorder) {
        return solve(preorder, inorder, 0, 0, inorder.size() - 1);
    }
};
// Using hashmap TC - O(N) SC - O(N)
class Solution {
public:
    TreeNode* solve(unordered_map<int, int>& mp, vector<int>& preorder, vector<int>& inorder, int preStart, int inStart, int inEnd)
    {
        if(preStart >= preorder.size() || inStart > inEnd) return NULL;
        TreeNode* root = new TreeNode(preorder[preStart]);
        // find the index of the root node in inorder array (we can use linear search as well)
        int inIndex = mp[root->val];
        // Now left part of inIndex will be left subtree of root and right part will be right subtree
        root->left = solve(mp, preorder, inorder, preStart + 1, inStart, inIndex - 1);
        // skip left tree (inIndex - inStart) to calculate the index of the right root node
        root->right = solve(mp, preorder, inorder, preStart + inIndex - inStart + 1, inIndex + 1, inEnd);
        return root;
    }
    TreeNode* buildTree(vector<int>& preorder, vector<int>& inorder) {
        unordered_map<int, int> mp;
        for(int i = 0; i < inorder.size(); i++)
        {
            mp[inorder[i]] = i;
        }
        return solve(mp, preorder, inorder, 0, 0, inorder.size() - 1);
    }
};
