# Code
```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    TreeNode* build(int preStart, int preEnd, vector<int>& preorder, 
    int inStart, int inEnd, vector<int>& inorder, unordered_map <int, int>& mp) {
        if (preStart > preEnd || inStart > inEnd)
            return NULL;
        TreeNode* root = new TreeNode(preorder[preStart]);
        int ind = mp[preorder[preStart]];    
        root->left = build(preStart+1, preStart+ind-inStart, preorder, inStart, ind-1, inorder, mp);
        root->right = build(preStart+ind-inStart+1, preEnd, preorder, ind+1, inEnd, inorder, mp);

        return root;   
    }

    TreeNode* buildTree(vector<int>& preorder, vector<int>& inorder) {
        int preStart = 0, preEnd = preorder.size() - 1;
        int inStart = 0, inEnd = inorder.size() - 1;
        unordered_map <int, int> mp;
        for (int i = 0; i < inorder.size(); i++)
            mp[inorder[i]] = i;

        return build(preStart, preEnd, preorder, inStart, inEnd, inorder, mp);    
    }
};
```

