        ans->left= help(pre,in,inLeft, ind-1);
        ans->right= help(pre,in,ind+1, inRight);
        return ans;
    }

    TreeNode* buildTree(vector<int>& pre, vector<int>& in) {
        return help(pre,in,0,in.size()-1);
