public TreeNode buildTree(int[] pre, int[] in) {
        TreeNode node = new TreeNode(pre[0]);
        Map<Integer, Integer> pos = new HashMap<>();
        Stack<TreeNode> stk = new Stack<>();
        int len = in.length, currPos, nextPos;

        for(int i=0; i<len; i++) pos.put(in[i], i);

        stk.push(node);
        for(int i=1; i<len; i++){

            TreeNode curr = stk.peek();
            currPos = pos.get(curr.val);
            TreeNode next = new TreeNode(pre[i]);
            nextPos = pos.get(pre[i]);

            if(nextPos < currPos) {
                curr.left = next;
                stk.push(next);
            } else{
                while(!stk.isEmpty() && pos.get((stk.peek()).val) < nextPos){
                    curr = stk.pop();
                }
                curr.right = next;
                stk.push(next);
            }
        }
        return node;
    }
```

