/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public TreeNode buildTree(int[] preorder, int[] inorder) {
        // Create a map of element-to-index mapping for inorder traversal
        Map<Integer,Integer> inMap=new TreeMap<>();
        for(int i=0;i<inorder.length;i++){
            inMap.put(inorder[i],i);
        }
        // Recursively construct the binary tree from preorder and inorder traversals
        TreeNode root=buildTree(preorder,0,preorder.length-1,inorder,0,inorder.length-1,inMap);
        return root;
    }

    private TreeNode buildTree(int[] preorder, int preStart, int preEnd, int[] inorder, int inStart, int inEnd, Map<Integer,Integer> inMap) {
        // Base case: the subtree is empty, return null
        if(preStart > preEnd || inStart > inEnd) return null;

        // Construct the root node with the first element in the preorder traversal
        TreeNode root = new TreeNode(preorder[preStart]);

        // Find the index of the root element in the inorder traversal using the map
        int inroot = inMap.get(root.val);

        // Calculate the number of elements in the left subtree
        int numLeft = inroot - inStart;

        // Recursively construct the left subtree
        root.left = buildTree(preorder, preStart+1, preStart+numLeft, inorder, inStart, inroot-1, inMap);

        // Recursively construct the right subtree
        root.right = buildTree(preorder, preStart+numLeft+1, preEnd, inorder, inroot+1, inEnd, inMap);

        // Return the root node of the constructed subtree
        return root;
    }
}

