   private int depth(TreeNode root, int level) {
        if (root == null) {
            return level;
        }

        return Math.max(depth(root.left, level + 1), depth(root.right, level + 1));
    }

    public String prettyPrint() {
        return printToGrid(this);
    }

    public String printToGrid(TreeNode root) {
        if (root == null) {
            return "";
        }
        final var depth = depth(this, 0);
        final var n = 2 * depth * depth;
        final var ca = new char[n][n];
        final var whiteLine = " ".repeat(n);
        for (var i = 0; i < n; ++i) {
            ca[i] = whiteLine.toCharArray();
        }
        printToGrid(ca, root, 0, ca[0].length / 2);
        var sb = new StringBuilder();
        for (var r : ca) {
            final var s = new String(r).stripTrailing();
            if (s.equals("")) {
                break;
            }
            sb.append(s).append("\n");
        }
        return sb.toString();
    }

    public void printToGrid( char[][] grid, TreeNode root, int row, int col) {
        if (root == null) {
            return;
        }
        final var depth = depth(root,0);
        final var v = root.val;
        final var s = Integer.toString(v);
        var writeCol = col - s.length() / 2;
        while (writeCol < grid.length - s.length() && grid[row][writeCol] != ' '
                || grid[row][writeCol - 1] != ' ') {
            ++writeCol;
        }
        for (var i = 0; i < s.length(); ++i) {
            grid[row][writeCol + i] = s.charAt(i);
        }
        final var spacing = Math.max(2, depth - (row/2) + 1);
        if (root.left != null) {
            grid[row + 1][col - spacing + 1] = '/';
            printToGrid(grid, root.left, row + 2, col - spacing);
        }
        if (root.right != null) {
            grid[row + 1][col + spacing - 1] = '\\';
            printToGrid(grid, root.right, row + 2, col + spacing);
        }
    }
