class Solution {
    public int[][] indexPairs(String text, String[] words) {
        Map<Character, Set<Integer>> map = new HashMap<>();
        for (int i = 0;i < text.length();i++) {
            map.computeIfAbsent(text.charAt(i), (v) -> new HashSet<>())
                .add(i);
        }
        List<int[]> answer = new ArrayList<>();
        for (String word : words) {
            for (int index : map.getOrDefault(word.charAt(0), new HashSet<>())) {
                int result = dfs(map, index, word, 1);
                if (result != -1) {
                    answer.add(new int[]{index, result});
                }
            }
        }
        int[][] result = new int[answer.size()][2];
        int j = 0;

        for (int[] a : answer) result[j++] = a;

        Arrays.sort(result, new Comparator<int[]>() {
            public int compare(int[] o1, int[]o2) {
                return o1[0] == o2[0] ? o1[1] - o2[1] : o1[0] - o2[0];
            }
        });

        return result;
    }

    public int dfs(Map<Character, Set<Integer>> map,
        int currIndex,
        String target, 
        int targetIndex) {
        if (targetIndex >= target.length()) {
            return currIndex;
        }

        if (map.containsKey(target.charAt(targetIndex)) 
            && map.get(target.charAt(targetIndex)).contains(currIndex+1)) {
                return dfs(map, currIndex+1, target, targetIndex+1);
            }
        return -1;
    }
}
