class Solution {
public:
    int dp[101][101];
    int n,m;
    int numSubmatrixSumTarget(vector<vector<int>>& matrix, int target) {
        n=matrix.size(),m=matrix[0].size();
        fun(matrix);
        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
               int si=i,sj=j;

                for(int p=si;p<n;p++){
                    for(int q=sj;q<m;q++){

                        if(p==si && q==sj){

                            ans+=(target==matrix[p][q])?1:0;
                        }else{

                             ans+=(target==getans(si,sj,p,q))?1:0;
                        }
                    }
                }

            }
        }
        return ans;
    }
   void fun(vector<vector<int>>& matrix){
        dp[0][0]=matrix[0][0];      // Getting the sum of matrix from index(0,0) for every (i,j);
        for(int i=1;i<m;i++){
            dp[0][i]=(dp[0][i-1]+matrix[0][i]);
        }
        for(int i=1;i<n;i++){
            dp[i][0]=(dp[i-1][0]+matrix[i][0]);
        }
        for(int i=1;i<n;i++){
            for(int j=1;j<m;j++){
                dp[i][j]=dp[i-1][j]+dp[i][j-1]-dp[i-1][j-1]+matrix[i][j];
            }
        }

    }

  int getans(int si,int sj,int ei,int ej){
        if(si==0 && sj==0){
            return dp[ei][ej];
        }else if(si==0 && sj!=0){
            return dp[ei][ej]-dp[ei][sj-1];
        }else if(si!=0 && sj==0){
            return dp[ei][ej]-dp[si-1][ej];
        }else{
             return dp[ei][ej]-dp[ei][sj-1]-dp[si-1][ej]+dp[si-1][sj-1];
        }

    }
};
