		int solve(vector<int>&nums,int k)
    {unordered_map<int,int>mp;
     int cs=0;
     int ans=0;
     for(int i=0;i<nums.size();++i)
     {
         cs+=nums[i];
         if(cs==k)
         {
            ans++; 
