class Solution {
    public int numSubmatrixSumTarget(int[][] matrix, int target)
    {
        int n=matrix.length;
        int m=matrix[0].length;
        long dp[][]=new long[n][m];
        for(int i=0;i<n;i++)
        {
            dp[i][0]=matrix[i][0];
        }
        for(int i=0;i<n;i++)
        {
            for(int j=1;j<m;j++)
            {
                dp[i][j]=dp[i][j-1]+matrix[i][j];
            }
        }
        int count=0;
        for(int start=0;start<m;start++)
        {
            for(int end=start;end<m;end++)
