class Solution {
public:
    int numSubmatrixSumTarget(vector<vector<int>>& matrix, int target) {
        const int m = matrix.size(), n = matrix[0].size();
        for(int i = 0; i < m; i++)
            for(int j = 1; j < n; j++)
                matrix[i][j] += matrix[i][j-1];

        int ans = 0;
        for(int l = 0; l < n; l++) // l: left column index
            for(int r = l; r < n; r++){ //r: right column index
                unordered_map<int, int> counts{{0, 1}};
                int cur = 0; //this guy working hard, coming from 1-D world, but can watch 2-D by collecting runnign rums of different rows between col l & r
                for(int i = 0; i < m; i++){
                    cur += matrix[i][r] - (l > 0 ? matrix[i][l - 1] : 0);                    
                    ans += counts[cur - target];
                    ++counts[cur];
                }                
            }
        return ans;        
    }
};
