The whole idea is to calculate the sum of the row prefix and the sum of the column prefix(using row prefix), the submatrix can be described by row & column prefix.
Yeah, I defined a special version of hashtable(linear probe) instead of using `unordered_map` in STL.
DO NOT USE `vector` OR `unordered_map` which may causing TLE (STL in Leetcode is not stable).
```
#define HT_LENGTH 256

struct HashTable {
    int keys[HT_LENGTH];
    int vals[HT_LENGTH];

    HashTable() { this->clear();}

    void clear() {
        keys[0] = 0; vals[0] = 1;
        for (int i = 1;i < HT_LENGTH;i++) keys[i] = -1;
    }

    int find(int key) {
        uint32_t ikey = (uint32_t)key % HT_LENGTH;
        for (int i = 0;i < HT_LENGTH;i++) {
            if (keys[ikey] == -1)  return 0;
            if (keys[ikey] == key) return vals[ikey];
            ikey = ++ikey == HT_LENGTH ? 0 : ikey;
        }
        return false;
    }

    void inc(int key) {
        uint32_t ikey = (uint32_t)key % HT_LENGTH;
        for (int i = 0;i < HT_LENGTH;i++) {
            if (keys[ikey] == -1) {
                keys[ikey] = key;
                vals[ikey] = 1;
                return;
            }
            if (keys[ikey] == key) {
                vals[ikey]++;
                return;
            }
            ikey = ++ikey == HT_LENGTH ? 0 : ikey;
        }
    }
};

class Solution {
private:
    HashTable ht;
public:
    int numSubmatrixSumTarget(vector<vector<int>>& matrix, int target) {
        int rprefix[100][101];
        int m = matrix.size(), n = matrix[0].size();
        int ret = 0;
        for (int i = 0;i < m;i++) {
            rprefix[i][0] = 0;
            for (int j = 1;j <= n;j ++) {
                rprefix[i][j] = rprefix[i][j - 1] + matrix[i][j - 1];
            }
        }

        for (int i = 1;i <= n;i++) {
            for (int j = i;j <= n;j++) {
                ht.clear();
                int cprefix = 0;
                for (int row = 0;row < m;row++) {
                    cprefix += rprefix[row][j] - rprefix[row][i - 1];
                    ret += ht.find(cprefix - target);
                    ht.inc(cprefix);
                }
            }
        }
        return ret;
    }
};
