class Solution {
    public int numSubmatrixSumTarget(int[][] matrix, int target) {
        NumMatrix nm = new NumMatrix(matrix);
        int count = 0;
        int r = matrix.length;
        int c = matrix[0].length;
        for(int i0 = 0 ; i0 < r; i0++){
            for(int j0 = 0 ; j0 < c; j0++){
                for(int i1 = i0 ; i1 < r; i1++){
                    for(int j1 = j0 ; j1 < c; j1++){
                       if(nm.sumRegion(i0,j0,i1,j1) == target){
                           count++;
                       }
                    }
                }
            }
        }
        return count;
    }
}

class NumMatrix {
    int[][] ps;
    public NumMatrix(int[][] matrix) {
      if (matrix == null || matrix.length == 0) return;
      ps = new int[matrix.length + 1][matrix[0].length + 1];
      int r = ps.length;
      int c = ps[0].length;
      for (int i = 1; i < r; i++) {
        for (int j = 1; j < c; j++) {
          int top = ps[i - 1][j];
          int left = ps[i][j - 1];
          int diag = ps[i - 1][j - 1];
          int current = matrix[i - 1][j - 1];
          ps[i][j] = current + top + left  - diag;
        }
      }
    }

    public int sumRegion(int x1, int y1, int x2, int y2) {
      if (ps == null) return 0;
      x2++;
      y2++;
      x1++;
      y1++;
      int current = ps[x2][y2];
      int top = ps[x1 - 1][y2];
      int left = ps[x2][y1 - 1];
      int diag = ps[x1-1][y1-1];
      return current - top - left + diag;
    }
}
