```cpp
class Solution {
public:
    int numSubmatrixSumTarget(vector<vector<int>>& matrix, int target) {
        size_t rows = matrix.size(), cols = matrix[0].size();

        // calculate prefix sum
        for (size_t row = 0; row < rows; ++row) {
            for (size_t col = 1; col < cols; ++col) {
                matrix[row][col] += matrix[row][col - 1];
            }
        }
        for (size_t col = 0; col < cols; ++col) {
            for (size_t row = 1; row < rows; ++row) {
                matrix[row][col] += matrix[row - 1][col];
            }
        }

        int cnt = 0;
        // calculate the sum of matrix[r1, r2][c1, c2]
        // target = matrix[r2][c2] - matrix[r1 - 1][c2] - matrix[r2][c1 - 1] + matrix[r1 - 1][c1 - 1]
        // ==> seperate c1 and c2
        // ==> matrix[r2][c2] - matrix[r1 - 1][c2] = target + (matrix[r2][c1 - 1] - matrix[r1 - 1][c1 - 1])
        for (size_t r1 = 0; r1 < rows; ++r1) {
            for (size_t r2 = r1; r2 < rows; ++r2) {
				// Instead of iteration by c1 then by c2 (this solution's time complxity is O(N^4)), 
				// cache data in hash table when iterating c and then find result in hash table during later iteration.
				// In this way, the time complxity reduces to O(N^3)
                unordered_multiset<int> sums;  // cache the sums of matrix[r1...r2][0...c] to reduce the loop by one layer
                for (size_t c = 0; c < cols; ++c) {
                    int sum = r1 == 0 ? matrix[r2][c] : matrix[r2][c] - matrix[r1 - 1][c];
                    if (sum == target) ++cnt;
                    cnt += sums.count(sum - target);
                    sums.insert(sum);
                }
            }
        }
        return cnt;
    }
};
