        //Here the magic begin
        int res = 0;
        for(int fixColumn = 0;fixColumn < matrix[0].size();fixColumn++)
        {
            for(int subMatrixCol = fixColumn;subMatrixCol < matrix[0].size();subMatrixCol++)
