class Solution {
public:
    // TC-> O(n*m^2)
    // SC-> O(n)
    int numSubmatrixSumTarget(vector<vector<int>>& matrix, int target) {
        int n = matrix.size();
        int m = matrix[0].size();

        // forming prefix -sum
        for(int i = 0; i < n; i++){
            for(int j = 1; j < m; j++){
                matrix[i][j] += matrix[i][j-1];
            }
        }

        int count = 0;
        // For each pair of columns, calculate the accumulated sum of rows.
        // Now this problem is same as, "Subarray sum with given Target Sum".
        for(int c1 = 0; c1 < m; c1++){
            for(int c2 = c1; c2 < m; c2++){
                unordered_map<int, int> mp; // sum->freq
                mp[0] = 1;
                // cout<<"c1="<<c1<<" "<<"c2="<<c2<<" ";
                int sum = 0;
                for(int r = 0; r < n; r++){
                    sum += matrix[r][c2] - (c1 > 0? matrix[r][c1-1]: 0);
                    // cout<<"r="<<r<<" "<<"sum="<<sum<<" ";
                    // there exist contiguous sum equal to target
                    if(mp.count(sum-target))
                        count += mp[sum-target];
                    // cout<<"count="<<count<<" ";
                    // add the sum obtained in the map
                    mp[sum]++;
                }
                // cout<<endl;
            }
        }
        return count;
    }
};
```

