class Solution {
public:
    int numSubmatrixSumTarget(vector<vector<int>>& m, int target) {

        int r = m.size(), c = m[0].size();
        int res = 0;
        for(int i = 1;i < c; i++)
            m[0][i] += m[0][i-1];
        for(int i = 1; i < r; i++)
            m[i][0] += m[i-1][0];
        for(int i = 1; i < r; i++)
            for(int j = 1; j < c; j++)
                m[i][j] += m[i-1][j] + m[i][j-1] - m[i-1][j-1];

