    public int numSubmatrixSumTarget(int[][] matrix, int target) {

        int m = matrix.length;
        int n = matrix[0].length;

        int[][] sum = new int[m][n];
        int sumSoFar = 0;
        for(int i=0;i<m;i++) {
            sumSoFar+=matrix[i][0];
            sum[i][0] = sumSoFar;
        }
        sumSoFar = 0;
        for(int i=0;i<n;i++) {
            sumSoFar+=matrix[0][i];
            sum[0][i] = sumSoFar;
        }

        for(int i=1; i< m;i++) {
            for(int j=1;j < n; j++) {
                sum[i][j] = sum[i-1][j] + sum[i][j-1] - sum[i-1][j-1] + matrix[i][j];
            }
        }
        int count =0;

        for(int i=0;i<m;i++) {
            for (int j=0;j<n;j++) {
                for(int k=i;k<m;k++) {
                    for (int l=j; l<n;l++) {
