    public int numSubmatrixSumTarget(int[][] matrix, int k) {
        int m = matrix[0].length, n = matrix.length;
        for(int i = 1; i<n; i++){
            for(int j = 0; j<m; j++){
                matrix[i][j] += matrix[i-1][j];
            }
