class Solution {
    public int numSubmatrixSumTarget(int[][] matrix, int target) {
        int m = matrix.length, n = matrix[0].length;
        int cnt = 0;
		//step1: compress the matrix to array for by row. 
        for (int i = 0; i < m; i++){
            int[] tmp = new int[n];
            for (int j = i; j < m; j++){
                for (int l = 0; l < n; l++){
                    tmp[l] += matrix[j][l];
                }
                cnt += helper(tmp, target);
            }
        }
        return cnt;
    }

    //this is same as solution for problem 560
    private int helper(int[] nums, int k){
        Map<Integer, Integer> map = new HashMap<>();
